from jax._src.api import value_and_grad
from jax._src.numpy.lax_numpy import array, clip, concatenate, cumsum, einsum, linspace, log, mean, prod, sort, vstack, zeros
from jax._src.nn.functions import softmax
from jax._src.random import PRNGKey, uniform, randint
from jax._src.tree_util import tree_map, tree_reduce
from jax.example_libraries.optimizers import adam

def jax_kron_prod(a, b):
    res = einsum('ij,ik->ijk', a, b)
    return res.reshape(-1, int(prod(array(res.shape[1:]))))

def jax_bin(z):
    x = feature.T[z].reshape(-1, 1)
    W = linspace(1, cut_points_list[z].shape[0] + 1, cut_points_list[z].shape[0] + 1).reshape(1, -1)
    b = cumsum(concatenate([zeros([1]), -sort(cut_points_list[z])]))
    h = x @ W + b
    return softmax(h / temperature)

def loss_fn(params):
    cut_points_list, leaf_score = params[:-1],  params[-1]
    leaf = tree_reduce(jax_kron_prod, tree_map(lambda z: jax_bin(z), [*range(feature.shape[1])]))
    preds = leaf @ leaf_score
    return -mean(label * log(clip(preds, epsilon, 1 - epsilon)))

