feature = vstack([array(i[0]) for i in iris])
label = vstack([array(i[1]) for i in iris])

epsilon = 1e-12
key = PRNGKey(0)
num_steps = 10
step_size = 1e-3
temperature = 0.1

cut_points_list = [uniform(key, [1]) for i in range(feature.shape[1])]
leaf_score = uniform(key, [int(prod(array([len(i) + 1 for i in cut_points_list]))), label.shape[1]])
opt_init, opt_update, get_params = adam(step_size)
params = cut_points_list + [leaf_score]
opt_state = opt_init(params)

for i in range(num_steps):
  value, grads = value_and_grad(loss_fn)(get_params(opt_state))
  opt_state = opt_update(i, grads, opt_state)
